import enchant# create a dictionary object for English languagedictionary = enchant.Dict("en_US")# ask user for a word to checkword = input("Enter a word to check spelling: ")# check if the word is in the dictionaryif dictionary.check(word):    print("The word is spelled correctly.")else:    suggestions = dictionary.suggest(word)    if len(suggestions) > 0:        print("Did you mean:")        for suggestion in suggestions:            print(suggestion)    else:        print("The word is not in the dictionary.")